tinymce.PluginManager.add('productpreview', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('productpreview', {
        text: false,
        icon: 'preview',
        onclick: function() {
            // Open window
            editor.windowManager.open({
                title: 'Preview Settings',
                width: 400,
                height: 70,
                body: [{
                    type: 'listbox',
                    name: 'type_id',
                    label: 'Kind',
                    values: [
                        { text: 'Product', value: 1 },
                        { text: 'News', value: 2 },
                        { text: 'Comment', value: 3 }
                    ],
                    onselect: function(e) {
                        editor.insertContent(this.value());
                    },
                }],
                onsubmit: function(e) {
                    let kind = e.data.type_id;
                    let current_url = window.location.pathname + window.location.search;
                    localStorage.setItem(current_url, editor.getContent({ format: 'raw' }));
                    window.open('/editor_preview?kind=' + kind + '&url=' + current_url, '_blank');
                }
            });
        }
    });

    return {
        getMetadata: function() {
            return {
                name: "Polygon Preview",
                url: "http://polygonvietnam.com"
            };
        }
    };
})